from sqlalchemy import DECIMAL, create_engine, Column, Integer, String, Float, JSON, DateTime, Boolean, ForeignKey,ARRAY
from sqlalchemy.orm import declarative_base
from sqlalchemy.orm import sessionmaker
from sqlalchemy.orm import relationship
from sqlalchemy.orm import scoped_session
from __init__ import Base
from datetime import datetime
class User(Base):
    __tablename__ = 'users'
    id = Column(Integer, primary_key=True)
    telegram_id = Column(Integer)
    username = Column(String, default='User')
    password = Column(String)
    deposit_balance = Column(Float, default=0)
    referal_balance = Column(Float, default=0)
    bonus_balance = Column(Float, default=0.5)
    bets = Column(String, default='')
    photo_of_user_url = Column(String, default='data:image/jpeg;base64,')
    total_amount_of_balance_replenishments = Column(Float, default=0.0)
    total_amount_of_money_withdrawn_from_the_balance = Column(Float, default=0.0)
    number_of_wins = Column(Integer, default=0)
    number_of_loses = Column(Integer, default=0)
    total_amount_of_money_won = Column(Float, default=0.0)
    total_amount_of_money_losed = Column(Float, default=0.0)
    was_invited_by = Column(Integer)
    invited_users = Column(JSON)

class Crash(Base):
    __tablename__ = 'crash'
    id = Column(Integer, primary_key=True)
    multiplier = Column(Float)
    profit = Column(Float, default=0)
    hash = Column(String)
    status = Column(Integer, default=0)
    created_at = Column(DateTime, default=datetime.now())

class CrashBets(Base):
    __tablename__ = 'crashbets'
    id = Column(Integer, primary_key=True)
    user_id = Column(Integer)
    round_id = Column(Integer)
    price = Column(Float)
    withdraw = Column(Float)
    won = Column(Float, default=0)
    status = Column(Integer, default=0)
    fake = Column(Integer, default=0)
    baltype = Column(String)
    created_at = Column(DateTime, default=datetime.now())

class Profit(Base):
    __tablename__ = 'profit'
    game = Column(String, primary_key=True)
    profit = Column(Float)

class Settings(Base):
    __tablename__ = 'settings'
    id = Column(Integer, primary_key=True)
    bank_mines = Column(Float, default=0)
    tg_channel = Column(String)
    domain = Column(String)
    sitename = Column(String)
    title = Column(String)
    description = Column(String)
    keywords = Column(String)
    site_disable = Column(Integer, default=0)
    support_link = Column(String)
    fake_bets = Column(Integer, default=0)
    fake_min_bet = Column(Float, default=0.1)
    fake_max_bet = Column(Float, default=100)
    profit_money = Column(Float, default=0)
    jackpot_comission = Column(Integer, default=0)
    min_bet = Column(Float, default=0.1)
    max_bet = Column(Float, default=100)
    crash_timer = Column(Integer, default=10)
    min_dep = Column(Float, default=1)
    ref_sum = Column(Float, default=0.5)
    min_ref_withdraw = Column(Float, default=10)
    
class allClients(Base):
    __tablename__ = 'allclients'
    id = Column(Integer, primary_key=True)
    client_sid = Column(String)

class Payments(Base):
    __tablename__ = 'payments'
    id = Column(Integer, primary_key=True)
    user_id = Column(Integer)
    amount = Column(Float)
    currency = Column(String)
    uuid = Column(String)
    description = Column(String)
    order_id = Column(String)
    status = Column(String)
    created_at = Column(DateTime)
    network = Column(String, default='tron')
    address = Column(String)
    status = Column(String)
    url = Column(String)
    
engine = create_engine('sqlite:///mydatabase.db', pool_size=10, max_overflow=20)


Base.metadata.create_all(engine)
# Создаем сессию SQLAlchemy
SessionFactory = sessionmaker(bind=engine)
